  const handleLogin = () => {
    setIsAuth(!isAuth);
  };

  const handleLogout = () => {};

  return (
    <AuthContext.Provider value={{ isAuth, handleLogin }}>
      {children}
    </AuthContext.Provider>
  );
}

export default AuthContextProvider;
